import React, { useState } from "react";
import axios from "axios";


const Register = () => {
    const [input, setInput] = useState(
        {
            username: "",
            email: "",
            password: "",
        }
    );

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post("http://localhost:9000/api/v1/user/register", input)
        } catch (error) {
            alert(error.response.data.message);
        }
    };

    return (
        <>
            <div className="container shadow">
                <h2 className="text-center my-3">Sign Up</h2>
                <div className="col-md-12 my-3 d-flex items-center justify-content-center">
                    <div className="row">
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="formgrpexampleinput" className="form-label">
                                    Name
                                </label>
                                <input
                                    type="text"
                                    name="username"
                                    value={input.username}
                                    onChange={
                                        (e) => setInput({ ...input, [e.target.name]: e.target.value })
                                    }
                                    className="form-control"
                                    id="formgrpexampleinput"
                                    placeholder="Enter Name"
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="formgrpexinput" className="form-label">
                                    Email
                                </label>
                                <input
                                    type="text"
                                    name="email"
                                    value={input.username}
                                    onChange={
                                        (e) => setInput({ ...input, [e.target.name]: e.target.value })
                                    }
                                    className="form-control"
                                    placeholder="Enter Email"
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="formgrpexampleinput" className="form-label">
                                    Password
                                </label>
                                <input
                                    type="password"
                                    name="password"
                                    className="form-control"
                                    onChange={
                                        (e) => setInput({ ...input, [e.target.name]: e.target.value })
                                    }
                                    id="formgrpexampleinput"
                                    placeholder="Enter Password"
                                />
                            </div>
                            <div className="mb-3">
                                <button type="submit" className="btn btn-primary btn-block">
                                    Login
                                </button>
                            </div>
                        </form>
                    </div>

                </div>
            </div>
        </>
    );
};

export default Register;
