import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";



const Login = () => {
    const navigate = useNavigate();
    const [input, setInput] = useState({
        email: "",
        password: "",
    });

    const handlelogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post("http://localhost:9000/api/v1/user/login", input);
            alert(res.data.message);
            // console.log(res.data);
            localStorage.setItem("token", res.data);
            localStorage.setItem("username", res.data.username);
            navigate("/");

        } catch (error) {
            alert(error.response.data.message);
        }
    }
    return (
        <>
            <div className="container shadow">
                <h2 className="text-center my-3">Enter in your Account</h2>
                <div className="col-md-12 my-3 d-flex items-center justify-content-center">
                    <div className="Row">

                        <form onSubmit={handlelogin}>
                            <div className="mb-3">
                                <label htmlFor="formGrpexinput" className="form-label">
                                    Email
                                </label>
                                <input
                                    type="text"
                                    name="email"
                                    value={input.email}
                                    onChange={
                                        (e) => setInput({ ...input, [e.target.name]: e.target.value })
                                    }
                                    className="form-control"
                                    id="formGrpexinput"
                                    placeholder="Enter Email"
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="formGrpexinput" className="form-label">
                                    Password
                                </label>
                                <input
                                    type="password"
                                    name="password"
                                    value={input.password}
                                    onChange={
                                        (e) => setInput({ ...input, [e.target.name]: e.target.value })
                                    }
                                    className="form-control"
                                    id="formGrpexinput"
                                    placeholder="Enter Password"
                                />
                            </div>
                            <div className="mb-3">
                                <button type="btn btn-primary btn-block">
                                    Login
                                </button>
                            </div>

                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Login;
