import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {



    const [blogs, setblogs] = useState([]);
    useEffect(() => {
        const fetchallblogs = async () => {
            const res = await axios.get("http://localhost:9000/api/v1/get/allblogs",
                {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("token")}`,

                    },
                }

            );
            setblogs(res.data);
        };
        fetchallblogs();
    }, []);

    return (
        <>
            <main className="my-5">
                <div className="container shadow-lg">
                    <section className="text-center">
                        <h2 className="mb-5 my-3">
                            <strong>Latest Posts</strong>
                        </h2>

                        <div className="row">
                            {blogs && blogs.length > 0 ?
                                blogs.map((item) => {
                                    return (
                                        <></>
                                    )
                                }) :
                                <h2>Loading....</h2>
                            }
                            <div className="col-lg-4 col-md-12 mb-4">
                                <div className="card">
                                    <div
                                        className="bg-image hover-overlay ripple"
                                        data-mdb-ripple-color="light"
                                    >
                                        <img
                                            src={`
                                        https://www.kindpng.com/picc/m/235-2350682_new-svg-image-small-user-login-icon-hd.png
                                        `}
                                            className="img-fluid"
                                            alt="login-icon"
                                        />
                                        <a href="#!">
                                            <div
                                                className="mask"
                                                style={{
                                                    backgroundColor: "rgba(251,251,251,0.15)",
                                                }}
                                            ></div>
                                        </a>
                                    </div>
                                    <div className="card-body">
                                        <h5 className="card-title">Demo</h5>
                                        <p className="card-text">Demo Content</p>
                                        <Link to="{`/blog/1`}" class="btn btn-primary"
                                        >
                                            Read More
                                        </Link>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                </div>
            </main>

            <footer className="bg-primary text-lg-start">
                <div
                    className="text-center p-3 text-white"
                    style={{ backgroundColor: "rgba(0,0,0,0.2)" }}
                >
                    Â© 2024 Copyright :
                    <a className="text-white mx-2" href="https://mdbootstrap.com/">
                        Blogify
                    </a>
                </div>
            </footer>
        </>
    );
};

export default Home;
